mapper.select:
select 

from D_SPO_ORDER_INFO
where ORDER_ID = #{orderId,jdbcType=VARCHAR}
  and CHANNEL = #{channel,jdbcType=VARCHAR}

mapper.delete:
delete from D_SPO_ORDER_INFO
where ORDER_ID = #{orderId,jdbcType=VARCHAR}
  and CHANNEL = #{channel,jdbcType=VARCHAR}

